import React, { useEffect, useState } from "react";
import {
	Badge,
	Breadcrumb,
	BreadcrumbItem,
	Col,
	Row,
	Spinner,
	Stack,
} from "react-bootstrap";
import JikanApi from "../../api/jikan.api";
import "./anime_page.component.css";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

const AnimePageComponent = (props) => {
	const anime_id = window.location.pathname.slice(7); // 50265
	let { animeId, setAnimeId } = props; // 50265
	const [animeDetails, setAnimeDetails] = useState(
		<div className="d-flex justify-content-center">
			<Spinner animation="border" className="m-3" />
		</div>
	);

	useEffect(() => {
		setAnimeDetails(
			<div className="d-flex justify-content-center">
				<Spinner animation="border" className="m-3" />
			</div>
		);

		// const delayDebounceFn = setTimeout(() => {
		JikanApi.get(`/anime/${anime_id}/full`)
			.then((res) => {
				let { data } = res.data;
				setAnimeDetails({
					mal_id: anime_id,
					aired: data.aired.string,
					premiered: `${
						data.season.charAt(0).toUpperCase() +
						data.season.slice(1)
					} ${data.year}`,
					duration: data.duration,
					status: data.status,
					title_japanese: data.title_japanese,
					title_synonyms: data.title_synonyms,
					title_english: data.title_english,
					title: data.title,
					genres: data.genres.map((genre) => {
						return { mal_id: genre.mal_id, name: genre.name };
					}),
					producers: data.producers.map((producer) => {
						return { mal_id: producer.mal_id, name: producer.name };
					}),
					studios: data.studios.map((studio) => {
						return { mal_id: studio.mal_id, name: studio.name };
					}),
					synopsis: data.synopsis,
					trailer: data.trailer.embed_url,
					members: data.members,
					score: data.score,
					popularity: data.popularity,
					type: data.type,
					rank: data.rank,
					image: data.images.webp.image_url,
					bg_image: data.images.webp.large_image_url,
					rating: data.rating,
				});
			})
			.catch((err) => {
				setAnimeDetails(
					<div className="d-flex justify-content-center">
						<p>No Anime Found</p>
					</div>
				);
			});

		const bttn = document.querySelector("button.More");
		const span = document.querySelector("#synopsis");
		console.log(span);
		bttn.dataset.state = 0;
		bttn.addEventListener("click", function (e) {
			span.dataset.tmp = span.textContent;
			span.textContent = span.dataset.content;
			span.dataset.content = span.dataset.tmp;

			this.innerHTML =
				this.dataset.state === 1 ? "Show More..." : "Show Less...";
			this.dataset.state = 1 - this.dataset.state;
		});

		span.dataset.content = span.textContent;
		span.textContent = span.textContent.substr(0, 100) + "...";

		// }, 1000);
		// return () => clearTimeout(delayDebounceFn);
	}, [animeId]);

	if (animeDetails.mal_id === undefined) {
		return animeDetails;
	} else {
		document.styleSheets[0].addRule(
			"div#anime_page_overlay:before",
			`background: url(${animeDetails.bg_image}) center/cover no-repeat`
		);
		return (
			<div id="anime_page_overlay">
				<Row className="mx-0">
					<Col lg={8}>
						<Breadcrumb>
							<Breadcrumb.Item
								linkAs={Link}
								linkProps={{ to: "/" }}
							>
								Home
							</Breadcrumb.Item>
							<Breadcrumb.Item
								linkAs={Link}
								linkProps={{ to: "#" }}
							>
								TV
							</Breadcrumb.Item>
							<Breadcrumb.Item active>
								{animeDetails.title}
							</Breadcrumb.Item>
						</Breadcrumb>
						{/* <h1>{animeDetails.title}</h1> */}
						<Stack direction="horizontal" gap="3">
							<img src={animeDetails.image} alt="" />
							<div>
								<h1>{animeDetails.title}</h1>
								<h4>{animeDetails.title_japanese}</h4>
								<Breadcrumb className="mx-auto">
									<Breadcrumb.Item active>
										<Badge text="dark">
											{animeDetails.rating}
										</Badge>
									</Breadcrumb.Item>
									<Breadcrumb.Item active>
										{animeDetails.type}
									</Breadcrumb.Item>
									<Breadcrumb.Item active>
										{animeDetails.duration}
									</Breadcrumb.Item>
								</Breadcrumb>
								<p id="synopsis">{animeDetails.synopsis}</p>
								<div>
									<button className="More">Show more</button>
								</div>
							</div>
						</Stack>
					</Col>
					<Col lg={4} style={{ height: "300px" }}></Col>
				</Row>
			</div>
		);
	}
};

export default AnimePageComponent;
