import React, { useEffect, useState } from "react";
import { Carousel } from "react-bootstrap";
import JikanApi from "../../../api/jikan.api";

const SeasonsNowComponent = () => {
	const [seasonsNow, setSeasonsNow] = useState([]);

	useEffect(() => {
		JikanApi.get("/seasons/now?limit=10&filter=airing")
			.then((res) => {
				// console.log(res.data.data);
				setSeasonsNow(
					res.data.data.map((anime) => {
						return {
							mal_id: anime.mal_id,
							image: anime.images.webp.large_image_url,
							title: anime.title,
						};
					})
				);
			})
			.catch((err) => {
				console.log(err);
			});
	}, [setSeasonsNow]);

	return (
		<div>
			{/* <h1>Fall 2022 Anime</h1> */}
			<Carousel>
				{seasonsNow.map((anime) => {
					<Carousel.Item>
						<img
							className="d-block w-100"
							src={anime.image}
							alt=""
						/>
						<Carousel.Caption>
							<h3>{anime.title}</h3>
						</Carousel.Caption>
					</Carousel.Item>;
				})}
			</Carousel>
		</div>
	);
};

export default SeasonsNowComponent;
