import React, { useEffect, useState } from "react";
import "./app.component.css";
import NavComponent from "./nav/nav.component";
import JikanApi from "../api/jikan.api";

const AppComponent = () => {
	const [search, setSearch] = useState("");
	const [searchResults, setSearchResults] = useState([]);

	useEffect(() => {
		if (!search) return setSearchResults([]);

		JikanApi.get(`/anime?q=${search}&limit=5`).then((res) => {
			setSearchResults(
				res.data.data.map((anime) => {
					return {
						mal_id: anime.mal_id,
						title: anime.title,
						image: anime.images.webp.small_image_url,
						rank: anime.rank,
						score: anime.score,
						type: anime.type,
					};
				})
			);
		});
	}, [search]);

	return <NavComponent setSearch={setSearch} searchResults={searchResults} />;
};

export default AppComponent;
