import React, { useEffect, useState } from "react";
import { Spinner } from "react-bootstrap";
import JikanApi from "../../../api/jikan.api";
import "./seasons.now.component.css";
import Carousel from "better-react-carousel";

const SeasonsNowComponent = () => {
	const [seasonsNow, setSeasonsNow] = useState("");
	const [seasonsNowList, setSeasonsNowList] = useState([]);

	useEffect(() => {
		const delayDebounceFn = setTimeout(() => {
			setSeasonsNowList(<Spinner animation="border" className="m-3" />);

			JikanApi.get("/seasons/now?limit=20&filter=airing").then((res) => {
				setSeasonsNowList(
					res.data.data.map((anime) => {
						return {
							mal_id: anime.mal_id,
							image: anime.images.webp.large_image_url,
							title: anime.title,
							season: anime.season,
							year: anime.year,
						};
					})
				);
			});
		}, 1000);

		return () => clearTimeout(delayDebounceFn);
	}, [seasonsNow]);

	const myDot = ({ isActive }) => {
		<span
			style={{
				display: "inline-block",
				height: isActive ? "8px" : "5px",
				width: isActive ? "8px" : "5px",
				background: "#1890ff",
			}}
		></span>;
	};

	return (
		<div className="d-flex flex-column align-items-center w-100">
			{seasonsNowList.length === undefined ? (
				seasonsNowList
			) : (
				<>
					<h1>
						{seasonsNowList.length === undefined ||
						seasonsNowList.length === 0
							? ""
							: `${seasonsNowList[0].season} ${seasonsNowList[0].year} Anime`}
					</h1>
					{/* <Carousel>
						{seasonsNowList.map((anime) => {
							return (
								<Carousel.Item key={anime.mal_id}>
									<img
										className="d-block w-100 seasonsNowImage"
										src={anime.image}
										alt=""
									/>
									<Carousel.Caption>
										<h3>{anime.title}</h3>
									</Carousel.Caption>
								</Carousel.Item>
							);
						})}
					</Carousel> */}
					<Carousel
						cols={5}
						rows={1}
						gap={10}
						loop
						autoplay={5000}
						dot={() => myDot(true)}
					>
						{seasonsNowList.map((anime) => {
							return (
								<Carousel.Item key={anime.mal_id}>
									<img
										className="d-block w-100 seasonsNowImage"
										src={anime.image}
										alt=""
									/>
								</Carousel.Item>
							);
						})}
					</Carousel>
				</>
			)}
		</div>
	);
};

export default SeasonsNowComponent;
