import React, { useState, useEffect } from "react";
import { Container, Spinner } from "react-bootstrap";
import JikanApi from "../../../api/jikan.api";
import "./top_anime.component.css";
import Carousel from "better-react-carousel";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

const TopAnimeComponent = () => {
	const [topAnime, setTopAnime] = useState([]);

	useEffect(() => {
		const delayDebounceFn = setTimeout(() => {
			JikanApi.get("/top/anime?filter=airing&type=tv").then((res) => {
				setTopAnime(
					res.data.data.map((anime) => {
						return {
							mal_id: anime.mal_id,
							image: anime.images.webp.large_image_url,
							title: anime.title,
							score: anime.score,
							popularity: anime.popularity,
							members: anime.members,
							rank: anime.rank,
						};
					})
				);
			});
		}, 1000);
		return () => clearTimeout(delayDebounceFn);
	}, []);

	return topAnime.length === 0 ? (
		""
	) : (
		<>
			<Container>
				<h1>Top Airing Anime</h1>
			</Container>
			<Carousel
				cols={5}
				rows={1}
				gap={10}
				loop
				autoplay={5000}
				showDots
				scrollSnap
			>
				{topAnime.map((anime) => {
					return (
						<Carousel.Item
							key={anime.mal_id}
							style={{ marginBottom: "0" }}
						>
							<Link
								id="item"
								className="rounded"
								style={{
									position: "relative",
									display: "block",
								}}
							></Link>
						</Carousel.Item>
					);
				})}
			</Carousel>
		</>
	);
};

export default TopAnimeComponent;
