import React, { useEffect, useState } from "react";
import { Spinner } from "react-bootstrap";
import JikanApi from "../../api/jikan.api";

const AnimePageComponent = () => {
	const anime_id = window.location.pathname.slice(7);
	const [animeDetails, setAnimeDetails] = useState(
		<div className="d-flex justify-content-center">
			<Spinner animation="border" className="m-3" />
		</div>
	);

	useEffect(() => {
		// const delayDebounceFn = setTimeout(() => {
		JikanApi.get(`/anime/${anime_id}/full`).then((res) => {
			let { data } = res.data;
			console.log(data);
			setAnimeDetails({
				mal_id: anime_id,
				aired: data.aired.string,
				premiered: `${
					data.season.charAt(0).toUpperCase() + data.season.slice(1)
				} ${data.year}`,
				duration: data.duration,
				status: data.status,
				score: data.score,
				title_japanese: data.title_japanese,
				title_synonym: data.title_synonyms[0],
			});
		});
		// }, 1000);
		// return () => clearTimeout(delayDebounceFn);
	}, []);

	return animeDetails.mal_id === undefined ? (
		animeDetails
	) : (
		<div>
			<h1>{animeDetails.title_japanese}</h1>
		</div>
	);
};

export default AnimePageComponent;
