import React, { useEffect, useState } from "react";
import { Carousel } from "react-bootstrap";
import JikanApi from "../../../api/jikan.api";

const SeasonsNowComponent = () => {
	const [seasonsNow, setSeasonsNow] = useState([]);

	useEffect(() => {
		JikanApi.get("/seasons/now?limit=10&filter=airing")
			.then((res) => {
				// console.log(res.data.data);
				setSeasonsNow(
					res.data.data.map((anime) => {
						return {
							mal_id: anime.mal_id,
							image: anime.images.webp.large_image_url,
							title: anime.title,
						};
					})
				);
			})
			.catch((err) => {
				console.log(err);
			});
	}, [seasonsNow]);

	return console.log("youve been here");
