	// 	<Carousel>
	// 		{seasonsNow.map((anime) => {
	// 			<Carousel.Item>
	// 				<img
	// 					className="d-block w-100"
	// 					src={anime.image}
	// 					alt=""
	// 				/>
	// 				<Carousel.Caption>
	// 					<h3>{anime.title}</h3>
	// 				</Carousel.Caption>
	// 			</Carousel.Item>;
	// 		})}
	// 	</Carousel>
	// </div>
};

export default SeasonsNowComponent;
