import React from "react";
import { Form, Nav } from "react-bootstrap";

const SearchComponent = (props) => {
	let { searchResults, setSearch, search } = props;

	return (
		<Nav className="position-relative col-lg-6">
			<div className="flex-grow-1">
				<Form.Control
					id="search"
					className="text-center"
					autoComplete="off"
					type="search"
					onChange={(e) => {
						setSearch(e.target.value);
					}}
					value={search}
				></Form.Control>
				{searchResults.length === 0 || (
					<div
						className="position-absolute shadow rounded w-100 d-flex flex-column"
						id="search_results"
						style={{ maxHeight: "30rem", overflowY: "auto" }}
					>
						{searchResults.map((result) => {
							return (
								<>
									<div
										className="text-white p-3 d-flex gap-3 align-items-center search_result"
										key={result.mal_id}
										style={{ cursor: "pointer" }}
									>
										<img
											src={result.image}
											alt=""
											style={{
												width: "50px",
												height: "50px",
											}}
										/>
										<div>
											<p
												className="m-0"
												style={{ overflowX: "hidden" }}
											>
												{result.title}
											</p>
											<p className="text-muted small m-0">
												<strong>Score:</strong>{" "}
												{result.score} |{" "}
												<strong>Rank:</strong> #
												{result.rank}
											</p>
											<p className="text-muted small m-0">
												<strong>Status:</strong>{" "}
												{result.status}
											</p>
										</div>
									</div>
									<hr className="m-0" />
								</>
							);
						})}
					</div>
				)}
			</div>
		</Nav>
	);
};

export default SearchComponent;
