import React, { useEffect, useState } from "react";
import { Container, Nav, Navbar, Spinner } from "react-bootstrap";
import "./nav.component.css";
import JikanApi from "../../api/jikan.api";
import SearchComponent from "./search.component/search.component";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

const NavComponent = (props) => {
	const [search, setSearch] = useState("");
	const [searchResults, setSearchResults] = useState([]);

	// for searching
	useEffect(() => {
		if (!search) return setSearchResults([]);

		const delayDebounceFn = setTimeout(() => {
			setSearchResults(<Spinner animation="border" className="m-3" />);

			JikanApi.get(`/anime?q=${search}&order_by=type&sort=desc`).then(
				(res) => {
					setSearchResults(
						res.data.data.length !== 0
							? res.data.data.map((anime) => {
									return {
										mal_id: anime.mal_id,
										title: anime.title,
										image: anime.images.webp
											.small_image_url,
										rank: anime.rank,
										score: anime.score,
										type: anime.type,
										status: anime.status,
									};
							  })
							: "No Anime Found"
					);
				}
			);
		}, 500);

		return () => clearTimeout(delayDebounceFn);
	}, [search]);

	return (
		<>
			<Navbar
				fixed="top"
				className="shadow"
				expand="lg"
				bg="dark"
				variant="dark"
				id="navbar"
			>
				<Container>
					<div className="col-lg-4">
						<Navbar.Brand href="/" className="primary">
							<strong className="text-primary">Anime</strong>
							List
						</Navbar.Brand>
					</div>
					<Navbar.Toggle aria-controls="basic-navbar-nav" />
					<Navbar.Collapse id="basic-navbar-nav">
						<SearchComponent
							searchResults={searchResults}
							setSearch={setSearch}
							search={search}
						/>
						<Nav className="col-lg-6 justify-content-end">
							<Nav.Link>Signup</Nav.Link>
							<Nav.Link
								className="text-primary primary"
								as={Link}
								to="/login"
							>
								Login
							</Nav.Link>
						</Nav>
					</Navbar.Collapse>
				</Container>
			</Navbar>
		</>
	);
};

export default NavComponent;
