import React, { useEffect, useState } from "react";
import { Carousel, Spinner } from "react-bootstrap";
import JikanApi from "../../../api/jikan.api";

const SeasonsNowComponent = () => {
	const [seasonsNow, setSeasonsNow] = useState("");
	const [seasonsNowList, setSeasonsNowList] = useState([]);

	useEffect(() => {
		const delayDebounceFn = setTimeout(() => {
			setSeasonsNowList(<Spinner animation="border" className="m-3" />);

			JikanApi.get("/seasons/now?limit=10&filter=airing").then((res) => {
				setSeasonsNowList(
					res.data.data.map((anime) => {
						return {
							mal_id: anime.mal_id,
							image: anime.images.webp.large_image_url,
							title: anime.title,
							season: anime.season,
							year: anime.year,
						};
					})
				);
			});
		}, 500);

		return () => clearTimeout(delayDebounceFn);
	}, [seasonsNow]);

	return (
		<div>
			<h1>
				{seasonsNowList.length === undefined
					? console.log("it should be here")
					: console.log("but why it is going here?")}
			</h1>
			<Carousel>
				{seasonsNowList.length === undefined
					? { seasonsNowList }
					: seasonsNowList.map((anime) => {
							return (
								<Carousel.Item key={anime.mal_id}>
									<img
										className="d-block w-100"
										src={anime.image}
										alt=""
									/>
									<Carousel.Caption>
										<h3>{anime.title}</h3>
									</Carousel.Caption>
								</Carousel.Item>
							);
					  })}
			</Carousel>
		</div>
	);
};

export default SeasonsNowComponent;
